import React, { useState, useEffect } from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import HomeIcon from '@mui/icons-material/Home';
import LogoutIcon from '@mui/icons-material/Logout';
import { Link as RouterLink, useLocation, useNavigate } from 'react-router-dom';
import { Box, Drawer, IconButton, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import httpService from '../../services/httpService'
import './libraryStyle.css'

const Library = () => {
  const navigate = useNavigate()
  const [purchasedMovies, setPurchasedMovies] = useState([]);

  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const toggleDrawer = (open) => (event) => {
      if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
      }
      setIsDrawerOpen(open);
  };

  useEffect(() => {
    async function fetchPurchasedMovies() {
      try {
        const response = await httpService.getLibrary(); // Recebe os IDs do usuÃ¡rio e dos filmes
        const data = await response.json();
  
        // Array para armazenar os detalhes dos filmes
        const moviesDetails = [];
  
        // Faz uma segunda chamada para obter os detalhes de cada filme com base nos IDs
        for (const movieId of data.moviesIds) {
          const movieResponse = await httpService.getMovieDetails(movieId);
          const movieData = await movieResponse.json();
  
          // Adiciona os detalhes do filme ao array moviesDetails
          moviesDetails.push(movieData);
        }
  
        // Atualiza o estado com os detalhes completos dos filmes
        setPurchasedMovies(moviesDetails);
      } catch (error) {
        console.error("Erro ao buscar filmes:", error);
      }
    }
  
    fetchPurchasedMovies();
  }, []);

  const handleSignOut = () => {
    localStorage.removeItem("token")
    navigate('/')
  }

  const EmptyMovies = ()=> {
    return (
      <Container sx={{ py: 3 }} maxWidth="md" className='no_movies'>Nenhum filme na biblioteca</Container>
    )
  }

  const list = (
    <Box
        sx={{ width: 250, bgcolor: "primary.main", minHeight: '100vh' }}
        onClick={toggleDrawer(false)}
        onKeyDown={toggleDrawer(false)}   
    >
        <List >
            <ListItem>
                <Button component={RouterLink} to="/home">
                    <ListItemIcon className='item_icon'>
                        <HomeIcon sx={{color: 'white'}}/>
                    </ListItemIcon>
                    <ListItemText primary="Home" className='text_menu'/>
                </Button>
            </ListItem>
            <ListItem>
                <Button onClick={handleSignOut}>
                    <ListItemIcon className='item_icon'>
                        <LogoutIcon sx={{color: 'white'}}/>
                    </ListItemIcon>
                    <ListItemText primary="Sair" className='text_menu'/>
                </Button>
            </ListItem>
        </List>
    </Box>
  );  
  
  const ListMovies = ({purchasedMovies}) => {
    return (
      <Container sx={{ py: 3 }} maxWidth="md">
        <Grid container spacing={4}>
          {purchasedMovies.map((movie, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card>
                <CardMedia
                  component="img"
                  height="300"
                  image={movie.coverUrl} 
                  alt={movie.title} 
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {movie.title}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    );
  };

    return (
      <>
        <CssBaseline />
        <AppBar position="relative">
          <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleDrawer(true)}>
            <MenuIcon />
          </IconButton>
            <Box className='moviePlay'>
                <Typography variant="h6" color="inherit" noWrap className='moviePlay_text'>
                    MoviePlay
                </Typography>
            </Box>
  
            <Box style={{ display: 'flex', alignItems: 'center', marginLeft: 'auto' }}>
                <AccountCircleIcon fontSize='large'/>
            </Box>
          </Toolbar>
        </AppBar>
        <Drawer anchor="left" open={isDrawerOpen} onClose={toggleDrawer(false)}>
            {list}
        </Drawer>

        <main>
          <Box style={{ backgroundColor: 'black', minHeight: '100vh', padding: '20px' }}>
            {purchasedMovies.length !== 0 ? (
              <ListMovies purchasedMovies={purchasedMovies} />
            ) : (
              <EmptyMovies />
            )}        
          </Box>
        </main>
        </> 
    );

}



export default Library
