import React, { useState, useEffect } from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import EditIcon from '@mui/icons-material/Edit';
import ClearIcon from '@mui/icons-material/Clear';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import HomeIcon from '@mui/icons-material/Home';
import LogoutIcon from '@mui/icons-material/Logout';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { Box, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Drawer, IconButton, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import httpService from '../../services/httpService'
import './libraryStyle.css'

const Library = ({location}) => {
  const [products, setProducts] = useState([])
  const navigate = useNavigate()
  const [open, setOpen] = React.useState(false)
  const [productIdToDelete, setProductIdToDelete] = useState(null)

  const { purchasedMovies } = location.state || { purchasedMovies: [] };

  const handleRemoveProduct = async () => {
    try {
      await httpService.deleteProduct(productIdToDelete);
      
      setProducts(prevProducts => prevProducts.filter(p => p.id !== productIdToDelete));
  
      handleClose();
    } catch (error) {
      console.error('Erro ao remover o produto:', error);
    }
  }

  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const toggleDrawer = (open) => (event) => {
      if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
      }
      setIsDrawerOpen(open);
  };

  const handleClickOpen = (productId) => {
    setProductIdToDelete(productId);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSignOut = () => {
    localStorage.removeItem("token")
  }

  const goTo = (path, params) => {
    return () => {
      navigate(path, {state: {product: params}})
    }
    
  }

  const EmptyMovies = ()=> {
    return (
      <Container sx={{ py: 3 }} maxWidth="md" className='no_movies'>No movies in the library</Container>
    )
  }

  const list = (
    <Box
        sx={{ width: 250, bgcolor: "primary.main", minHeight: '100vh' }}
        onClick={toggleDrawer(false)}
        onKeyDown={toggleDrawer(false)}   
    >
        <List >
            <ListItem>
                <ListItemIcon>
                    <AccountCircleIcon sx={{color: 'white'}} />
                </ListItemIcon>
                <ListItemText primary="Account" sx={{color: 'white'}} />
            </ListItem>
            <ListItem>
                <Button component={RouterLink} to="/home">
                    <ListItemIcon className='item_icon'>
                        <HomeIcon sx={{color: 'white'}}/>
                    </ListItemIcon>
                    <ListItemText primary="Home" className='text_menu'/>
                </Button>
            </ListItem>
            <ListItem>
                <Button onClick={handleSignOut}>
                    <ListItemIcon className='item_icon'>
                        <LogoutIcon sx={{color: 'white'}}/>
                    </ListItemIcon>
                    <ListItemText primary="Logout" className='text_menu'/>
                </Button>
            </ListItem>
        </List>
    </Box>
  );  
  
  const ListMovies = () => {
    return (
      <Container sx={{ py: 3 }} maxWidth="md">
        <Grid container spacing={4}>
          {purchasedMovies.map((movie, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card>
                <CardMedia
                  component="img"
                  height="300"
                  image={movie.image} // Suponhamos que o objeto movie tenha uma propriedade image com o link da imagem
                  alt={movie.title} // Suponhamos que o objeto movie tenha uma propriedade title com o tÃ­tulo do filme
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {movie.title}
                  </Typography>
                  {/* Aqui vocÃª pode adicionar mais informaÃ§Ãµes do filme se necessÃ¡rio */}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    );
  };

  useEffect(() => {
    async function fetchProducts() {
      try{
        const response = await httpService.getProducts()
        const products = await response.json()
        setProducts(products)
        //setLoading(false)
      } catch (error) {
        console.error("Erro")
        //setLoading(false)
      }
    }
    fetchProducts()
  }, [])

    return (
      <>
        <CssBaseline />
        <AppBar position="relative">
          <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleDrawer(true)}>
            <MenuIcon />
          </IconButton>
            <Box className='moviePlay'>
                <Typography variant="h6" color="inherit" noWrap className='moviePlay_text'>
                    MoviePlay
                </Typography>
            </Box>
  
            <Box style={{ display: 'flex', alignItems: 'center', marginLeft: 'auto' }}>
                <AccountCircleIcon fontSize='large'/>
            </Box>
          </Toolbar>
        </AppBar>
        <Drawer anchor="left" open={isDrawerOpen} onClose={toggleDrawer(false)}>
            {list}
        </Drawer>

        <main>
          {products.length !== 0 ?<ListMovies/> :  <EmptyMovies/>}         
        </main>
        </> 
    );

}



export default Library
