import React, { useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import LogoutIcon from '@mui/icons-material/Logout';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import Card from '@mui/material/Card';
import { Box, CardContent, CardMedia, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Drawer, IconButton, List, ListItem, ListItemIcon, ListItemText, Paper } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import VideoLibraryIcon from '@mui/icons-material/VideoLibrary';
import './homeStyle.css'
import httpService from '../../services/httpService'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'

const Home = () => {
    const navigate = useNavigate()
    const [isDrawerOpen, setIsDrawerOpen] = useState(false);
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [openDialog, setOpenDialog] = useState(false);
    const [purchasedMovies, setPurchasedMovies] = useState([]);

    const toggleDrawer = (open) => (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
        return;
        }
        setIsDrawerOpen(open);
    };

    const handleSignOut = () => {
        localStorage.removeItem("token")
        navigate('/')
    }

    const handlePurchase = (movieTitle) => {
        setOpenDialog(true);
        setSelectedMovie(movieTitle);
    };

    const handleCloseDialog = (selectedMovie) => {
        const updatedMovie = {
            title: selectedMovie.title, // Certifique-se de que 'selectedMovie' possui a propriedade 'title'
            image: selectedMovie.image, // Certifique-se de que 'selectedMovie' possui a propriedade 'image'
          };
      
          const updatedMovies = [...purchasedMovies, updatedMovie];
          setPurchasedMovies(updatedMovies);
          navigate('/library', { state: { purchasedMovies: updatedMovies } });
    };

    const Cards = (cardsData) => (
        <Box style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap' }}>
          {cardsData.map((card, index) => (
            <Card key={index} sx={{ maxWidth: 300, mt: 5 }}>
              <CardMedia
                component="img"
                height="250"
                image={card.image}
                alt={card.alt}
              />
              <CardContent>
                <Typography gutterBottom component="div" className='title'>
                  {card.title}
                </Typography>
                <Button type="submit" sx={{ mt: 1 }} variant="outlined" size="small" onClick={() => handlePurchase(card.title)}>
                    Purchase
                </Button>
              </CardContent>
            </Card>
          ))}
            <Dialog
                open={openDialog}
                onClose={() => handleCloseDialog(false)}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >   
                <DialogTitle id="alert-dialog-title">Confirm purchase</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Are you sure you want to buy "{selectedMovie}"?
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => handleCloseDialog(false)} color="primary">
                        No
                    </Button>
                    <Button onClick={() => handleCloseDialog(true)} color="primary" autoFocus>
                        Yes
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    )

    const cardsData = [
        {
          title: 'Divergent',
          image: 'https://upload.wikimedia.org/wikipedia/pt/8/8e/Divergent.png',
        },
        {
            title: 'BlackSwan',
            image: 'https://media.fstatic.com/HQpqPU9DQMjIN2TvyOGvK8SEI4I=/210x312/smart/filters:format(webp)/media/movies/covers/2015/03/cisne-negro_t16349.jpg',
        },
        {
            title: 'The Kissing Booth ',
            image: 'https://br.web.img2.acsta.net/pictures/18/05/04/15/03/2133793.jpg',
        },
        {
            title: 'Purple Hearts',
            image: 'https://bingeddata.s3.amazonaws.com/uploads/2022/07/purple-hearts.jpg',
        },
        {
            title: 'M : i : III',
            image: 'https://media.fstatic.com/-EmsH_7pHxkJeer-xtpRgpJLPho=/322x478/smart/filters:format(webp)/media/movies/covers/2018/07/mission-impossible-iii-5217e3b9e38f6.jpg',
        },
        {
            title: 'Hunger Games',
            image: 'https://upload.wikimedia.org/wikipedia/pt/4/42/HungerGamesPoster.jpg',
        },
        {
            title: 'Barbie',
            image: 'https://s2-marieclaire.glbimg.com/T_DX6ffqPGvmu5rvV2neO9uK1mM=/0x0:1382x2048/984x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_51f0194726ca4cae994c33379977582d/internal_photos/bs/2023/u/n/y0xBC1SIy2idfpVSornQ/barbie-posteres.jpg',
        },
    ];

    const additionalCardsData = [
        {
          title: 'The Conjuring',
          image: 'https://upload.wikimedia.org/wikipedia/pt/a/ac/The_Conjuring.jpg',
        },
        {
            title: 'Twilight',
            image: 'https://upload.wikimedia.org/wikipedia/pt/c/c1/Twilight_Poster.jpg',
        },
        {
            title: 'WALL-E',
            image: 'https://www.cafecomfilme.com.br/media/k2/items/cache/424a47d26101c371e8179dbd651fddc0_XL.jpg'
        },
        {
            title: 'White Chicks',
            image: 'https://upload.wikimedia.org/wikipedia/pt/thumb/d/de/White_chicks.jpeg/230px-White_chicks.jpeg',
        },
        {
            title: 'Annabelle',
            image: 'https://upload.wikimedia.org/wikipedia/pt/9/9b/Annabelle-poster.jpg',
        },
        {
            title: 'Cars',
            image: 'https://thiemeyoshino.com.br/wp-content/uploads/2020/04/214920140608-uau-posters-filmes-infantis-animacao-carros-cars-3.jpg',
        },
        {
            title: 'Scream',
            image: 'https://upload.wikimedia.org/wikipedia/pt/0/0e/Scream-5.png',
        },
    ];

    const additionalCardsData2 = [
        {
          title: 'My Fault',
          image: 'https://i.topflix.vc/testme/dle2/posts/2023-06/424122299.jpg',
        },
        {
            title: 'IT',
            image: 'https://upload.wikimedia.org/wikipedia/pt/8/82/It_2017.jpg',
        },
        {
            title: 'Cinderella',
            image: 'https://upload.wikimedia.org/wikipedia/pt/c/c2/Cinderella_2015_official_poster.jpg'
        },
        {
            title: 'Grown Ups',
            image: 'https://2.bp.blogspot.com/_gWDsadO9YPg/TLM3LXeh1HI/AAAAAAAAAFw/lAnWmNRg8Ww/s1600/grownupsver3.jpg',
        },
        {
            title: 'Red Notice',
            image: 'https://www.itaucinemas.com.br/_img/_filmes/4461_capa.jpg?Alerta-Vermelho',
        },
        {
            title: 'MEGAN',
            image: 'https://play-lh.googleusercontent.com/yhdgTfK0Yt3_wvJBtasKAVHVJ2B9GjAodsJPkWwDwDRemt0rS36aj96VWGAEFlqyOyKe5qdY7JfHzKw4wlUg',
        },
        {
            title: 'Rio',
            image: 'https://upload.wikimedia.org/wikipedia/en/thumb/b/bb/Rio2011Poster.jpg/220px-Rio2011Poster.jpg',
        },
    ];

    const list = (
        <Box
            sx={{ width: 250, bgcolor: "primary.main", minHeight: '100vh' }}
            onClick={toggleDrawer(false)}
            onKeyDown={toggleDrawer(false)}   
        >
            <List >
                <ListItem>
                    <ListItemIcon>
                        <AccountCircleIcon sx={{color: 'white'}} />
                    </ListItemIcon>
                    <ListItemText primary="Account" sx={{color: 'white'}} />
                </ListItem>
                <ListItem>
                    <Button component={RouterLink} to="/library">
                        <ListItemIcon className='item_icon'>
                            <VideoLibraryIcon sx={{color: 'white'}}/>
                        </ListItemIcon>
                        <ListItemText primary="Library" className='text_menu'/>
                    </Button>
                </ListItem>
                <ListItem>
                    <Button onClick={handleSignOut}>
                        <ListItemIcon className='item_icon'>
                            <LogoutIcon sx={{color: 'white'}}/>
                        </ListItemIcon>
                        <ListItemText primary="Logout" className='text_menu'/>
                    </Button>
                </ListItem>
            </List>
        </Box>
    );

  return (
    <>
        <CssBaseline />
        <AppBar position="relative">
          <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={toggleDrawer(true)}>
            <MenuIcon />
          </IconButton>
            <Box className='moviePlay'>
                <Typography variant="h6" color="inherit" noWrap className='moviePlay_text'>
                    MoviePlay
                </Typography>
            </Box>
  
            <Box style={{ display: 'flex', alignItems: 'center', marginLeft: 'auto' }}>
                <AccountCircleIcon fontSize='large'/>
            </Box>
          </Toolbar>
        </AppBar>
        <Drawer anchor="left" open={isDrawerOpen} onClose={toggleDrawer(false)}>
            {list}
        </Drawer>

        <main>
            <Box style={{ backgroundColor: 'black', minHeight: '100vh', padding: '20px' }}>
                <Typography className='category' style={{ marginTop: '20px'}}>
                    Movies
                </Typography>
                <Box>
                    {Cards(cardsData)}
                </Box>
                <Box>
                    {Cards(additionalCardsData)}
                </Box>
                <Box>
                    {Cards(additionalCardsData2)}
                </Box>
            </Box>
        </main>
    </> 
  )
}

export default Home
