import React from 'react'
import { Avatar, Button, Container, Grid, TextField, Typography, InputAdornment, IconButton } from "@mui/material"
import { Box } from "@mui/system"
import PersonIcon from '@mui/icons-material/Person';
import { Component } from "react";
import Paper from '@mui/material/Paper'
import './style.css';
import { Link } from 'react-router-dom'
import VisibilityIcon from '@mui/icons-material/Visibility'
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff'

class SignUp extends Component{
  constructor(props) {
    super(props);
    this.state = {
      showPassword: false,
      password: ''
    };
  }

  handlePasswordVisibility = () => {
    const updatedShowPassword = !this.state.showPassword
  this.setState({ showPassword: updatedShowPassword })
  return updatedShowPassword
  };

  handlePasswordChange = (e) => {
    this.setState({ password: e.target.value });
  };

  handleRegisterUser () {
    
  }
  
  render() {

    return (
      <Paper elevation={3} className="paper-container">
      <Container component="main">
        <Box component="form" display="flex" flexDirection="column" alignItems="center" justifyContent="center">
          <Avatar sx={{bgcolor: "secondary.main"}}><PersonIcon/></Avatar>
          <Typography variant="h5"> Sign Up</Typography>
          <Box sx={{mt: 2}} component="form" display="flex" flexDirection="column" alignItems="center" justifyContent="center">
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <TextField required fullWidth name="firstName" label="First Name" id="firstName"/>
              </Grid>
              <Grid item xs={6}>
                <TextField required fullWidth name="lastName" label="Last Name" id="lastName"/>
              </Grid>
            </Grid>
            <TextField sx={{mt: 2}} required fullWidth name="email" type="email" label="Email"/>
            <TextField 
              sx={{mt: 2}} 
              required
              fullWidth
              margin="normal"
              name="password"
              type={this.state.showPassword ? 'text' : 'password'}
              label="Password"
              value={this.password}
              onChange={this.handlePasswordChange}
              InputProps={{
                  endAdornment: (
                  <InputAdornment position="end">
                      <IconButton onClick={() => {
                        const updatedShowPassword = this.handlePasswordVisibility();
                        this.setState({ showPassword: updatedShowPassword });
                      }} edge="end">
                      {this.showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}
                      </IconButton>
                  </InputAdornment>
                  ),
              }}
            />
            <Button sx={{bgcolor: "secondary.main", mt: 2}} fullWidth variant="contained" to='/'> Send </Button>
            <Grid sx={{mt: 2}}>
              <Link to='/'> Already have an account? Log in</Link> 
            </Grid>
          </Box>
        </Box>
      </Container>
      </Paper>
    )
  }
}

export default SignUp
