import { Route, Router, RouterProvider, Routes, createBrowserRouter } from 'react-router-dom';
import {ThemeProvider, createTheme} from '@mui/material/styles'
import './App.css';
import Login from './pages/Login/Login';
import CreateUser from './pages/CreateUser/CreateUser'
import Home from './pages/Home/Home'
import CreateProduct from './pages/CreateProduct/CreateProduct'
import CreatePromotion from './pages/CreatePromotion/CreatePromotion'

function App() {
  const defaultRouter = createBrowserRouter([
    {
      path: "/",
      element: <Login/>
    },
    {
      path: "/user/create",
      element: <CreateUser/>
    },
    {
      path: "/home",
      element: <Home/>
    },
    {
      path: "/product/create",
      element: <CreateProduct/>
    },
    {
      path: "/promotion/create",
      element: <CreatePromotion/>
    },
  ])

  const defaultTheme = createTheme({
    palette: {
        primary: {
          main: '#87A46A'
        }, 
        secondary: {
          main: '#556B45'
        }
    }
  });

  const [products, setProducts] = useState([]);

  const handleProductCreated = (newProduct) => {
    // Adiciona o novo produto ao estado
    setProducts([...products, newProduct]);
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <div className="App">
        <Router>
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/user/create" element={<CreateUser />} />
            {/* Passa o estado products e a função handleProductCreated como propriedades para o componente Home */}
            <Route
              path="/home"
              element={<Home products={products} onProductCreated={handleProductCreated} />}
            />
            <Route path="/product/create" element={<CreateProduct onProductCreated={handleProductCreated} />} />
            <Route path="/promotion/create" element={<CreatePromotion />} />
          </Routes>
        </Router>
      </div>
    </ThemeProvider>
  )
}

export default App;
